import { Component } from '@angular/core';
import {MatProgressBarModule} from '@angular/material/progress-bar';
import {MatButtonModule} from '@angular/material/button';
import {MatDividerModule} from '@angular/material/divider';
import {MatCardModule} from '@angular/material/card';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css'],
  standalone: true,
  imports: [MatCardModule, MatDividerModule, MatButtonModule, MatProgressBarModule],
})

export class CardComponent {
  longText = `Water all plants in garden. Tomatoes need half liter of water per plant every two days.
  Cucumbers need 1 liter of water every day. Paprika needs 1 liter of water every 3 days.`;
  taskName: string = "Water plants"
}
