import { Component } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import {MatDialog, MatDialogConfig, MatDialogModule} from '@angular/material/dialog';
import { ActionsDialogComponent } from '../actions-dialog/actions-dialog.component';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css'],
  standalone: true,
  imports: [MatButtonModule, MatDialogModule, MatDialogConfig]
})
export class MenuComponent {

  private dialogConfig: MatDialogConfig = {
    data: {
      showDialogType: ''
    }
  };
  constructor(public dialog: MatDialog) {}

  createNewColumn() {
    this.dialogConfig.data.showDialogType = 'create';

    const dialogRef = this.dialog.open(ActionsDialogComponent, this.dialogConfig);

    dialogRef.afterClosed().subscribe(result => {
      console.log('Dialog closed with result:', result);
    });
  }

  filterColumns() {
    this.dialogConfig.data.showDialogType = 'edit';

    const dialogRef = this.dialog.open(ActionsDialogComponent, this.dialogConfig);

    dialogRef.afterClosed().subscribe(result => {
      console.log('Dialog closed with result:', result);
    });
  }

  deleteColumn() {
    this.dialogConfig.data.showDialogType = 'delete';

    const dialogRef = this.dialog.open(ActionsDialogComponent, this.dialogConfig);

    dialogRef.afterClosed().subscribe(result => {
      console.log('Dialog closed with result:', result);
    });
  }

}
