import { Component, Input } from '@angular/core';
import {MatProgressBarModule} from '@angular/material/progress-bar';
import {MatButtonModule} from '@angular/material/button';
import {MatDividerModule} from '@angular/material/divider';
import {MatCardModule} from '@angular/material/card';
import { Card } from '../models/card';
import { MatDialog, MatDialogConfig } from '@angular/material/dialog';
import { ActionsDialogComponent } from '../actions-dialog/actions-dialog.component';
import { CardService } from '../services/card.service';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css'],
  standalone: true,
  imports: [MatCardModule, MatDividerModule, MatButtonModule, MatProgressBarModule]
})

export class CardComponent {

  @Input() card: Card | undefined;
  private dialogConfig: MatDialogConfig = {
    data: {
      dialogType: '',
      card: undefined
    }
  };

  constructor(public dialog: MatDialog, private cardService: CardService) {}

  openDialog(dialogType: string, card: Card) {
    this.dialogConfig.data.dialogType = dialogType;
    this.dialogConfig.data.card = card;

    const dialogRef = this.dialog.open(ActionsDialogComponent, this.dialogConfig);

    dialogRef.afterClosed().subscribe(result => {

      if (result === 'delete-card') {
        console.log("card delete");
      }
    });
  }

  deleteCard(cardName: string) {
    if (this.card?.cardName === cardName) {
      this.cardService.deleteCard(this.card);
    } else {
      console.error('Card is undefined or null.');
    }
  }
}
