import { Injectable } from '@angular/core';
import { Card } from '../models/card';
import { State } from '../enums/state';
import { HttpClient } from '@angular/common/http'

@Injectable({
  providedIn: 'root'
})
export class CardService {
  constructor(
    private httpClient: HttpClient
  ) { }

  private cardList: Card[] = [
    {cardName: "Water plants", cardDescription: `Water all plants in garden. Tomatoes need half liter of water per plant every two days.
    Cucumbers need 1 liter of water every day. Paprika needs 1 liter of water every 3 days.`, cardState: State.TO_DO, taskHandler: "Marin"},
    {cardName: "Shorten lawn", cardDescription: `Shorten lawn to half inch length. Use electric trimmer and after that water lawn.`,
     cardState: State.TO_DO, taskHandler: "Dinka"},
    {cardName: "Clean house", cardDescription: `Vacuum all the floors and wipe the dust from the furniture. Wash windows and shutters.`,
     cardState: State.DONE, taskHandler: "Dinka"},
    {cardName: "Prepare launch", cardDescription: `Go for groceries in market. Prepare all vegetables for soup and put meat in the owen.`,
     cardState: State.IN_PROGRESS, taskHandler: "Marin"}
  ];

  getCards(): Card[] {
    console.log("getcards")
    this.httpClient.get('http://localhost:8080/cards').subscribe((data) => console.log(data))
    return this.cardList;
  }

  deleteCard(card: Card): void {
    const index = this.cardList.indexOf(card);
    if (index !== -1) {
      this.cardList.splice(index, 1);
    }
  }
}
