import { Component, Input, OnInit } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { CardComponent } from '../card/card.component';
import { TaskColumn } from '../models/task-column';
import { MatDialog, MatDialogConfig } from '@angular/material/dialog';
import { ActionsDialogComponent } from '../actions-dialog/actions-dialog.component';
import { CardService } from '../services/card.service';
import { Card } from '../models/card';
import { NgFor, NgIf } from '@angular/common';
import { TaskColumnService } from '../services/task-column.service';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-task-column',
  templateUrl: './task-column.component.html',
  styleUrls: ['./task-column.component.css'],
  standalone: true,
  imports: [MatButtonModule, CardComponent, NgFor, NgIf]
})
export class TaskColumnComponent implements OnInit {
  @Input() column: TaskColumn | undefined;
  private dialogConfig: MatDialogConfig = {
    data: {
      dialogType: ''
    }
  };

  cards: Card[] = [];

  constructor(public dialog: MatDialog,
  private cardService: CardService) {}

  ngOnInit(): void {
      this.getCards();
    }

  public getCards(): void {
    this.cardService.getCards().subscribe(
      (response: Card[]) => {
        this.cards = response.filter(card => card.cardState === this.column?.columnState);
        console.log(this.cards);
      },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
    );
  }


  openDialog(dialogType: string) {
    this.dialogConfig.data.dialogType = dialogType;

    const dialogRef = this.dialog.open(ActionsDialogComponent, this.dialogConfig);

    dialogRef.afterClosed().subscribe(result => {
      console.log('Dialog closed with result:', result);
    });
  }

  deleteColumn(columnName: string) {
    if (this.column?.columnName === columnName) {
      //this.columnService.deleteColumn(this.column);
    } else {
      console.error('Column is undefined or null.');
    }
  }
}
