import { Component } from '@angular/core';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {

  createNewColumn() {
    console.log("new column");
  }

}
<div class="menu">
  <ul>
    <li><select name="columns" class="filter-columns" placeholder="Filter columns" size="1" multiple>
      <option value="column_state"></option>
    </select></li>
    <li><button class="create-column" (click) = "createNewColumn()">Create new column</button></li>
  </ul>
</div>
