import { Component, Inject } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';

@Component({
  selector: 'app-actions-dialog',
  templateUrl: './actions-dialog.component.html',
  styleUrls: ['./actions-dialog.component.css'],
  standalone: true,
  imports: [MatButtonModule]
})
export class ActionsDialogComponent {
  constructor(@Inject(MAT_DIALOG_DATA) public data: any) { }

  ngOnInit() {
    if (this.data.showDialogType === 'create') {
      // Show content or behavior for type1
    } else if (this.data.showDialogType === 'filter') {
      // Show content or behavior for type2
    } else if (this.data.showDialogType === 'delete') {
    }
  }
}
<h1 mat-dialog-title class="dialog-title">Create new task column</h1>
<div mat-dialog-content class="form-container">
    <form>
        <div class="form-group">
          <label for="inputField">Task column name:</label>
          <input type="text" id="inputField" name="inputField" placeholder="Enter task column name"><br>
        </div>

        <div class="form-group">
          <label for="selectField">Select state:</label>
          <select id="selectField" name="selectField">
            <option value="" disabled selected>Select an option</option>
            <option value="option1">TODO</option>
            <option value="option2">In Progress</option>
            <option value="option3">Done</option>
          </select>
        </div><br>

        <button mat-button class="submit_btn">Submit</button>
      </form>
</div>
